import React from 'react'
function FunctionClick(){ //with jsx you pass  function as the eventHandler rather than a string
    function clickHandler(){
        console.log('Button clicked');
    }
    return(   //React Events are named using camelCase
        <div>  
        <button  onClick = {clickHandler()}>click</button>
        </div>
    )
}
export default FunctionClick
