import React,{Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css/';
import Button from 'react-bootstrap/Button'
class Another extends Component{
    constructor (props){
        super(props)
        this .state = {
            firstName:"",
            lasrName:"",
            password:"",
            gender:""
        }
        this.handleSubmit = this.handleSubmit.bind(this)
    }                                        //./CSS/todo.css
    firsthandler = (event)=>{
        this.setState({
            firstName:event.target.value
        })
    }
    lasthandler = (event)=>{
        this.setState({
            lastName:event.target.value
        })
    }
    genderhandler = (event)=>{
      this.setState({
          gender:event.target.value
      })
    }
    handleSubmit = (event)=>{
        alert(` ${this.state.firstName} ${this.state.lastName} Regestered successfully !!!`)
        console.log(this.state);
        this.setState({
            firstName:"",
            lastName:"",
            password:"",
            gender:""   
        })
        event.preventDefault()   
    }
    render(){
     return(
         <div>
             <form onSubmit = {this.handleSubmit}>
                 <h1 style = {{color:'aqua'}}>User Registration</h1>
                 <label>firstName:</label> <input type = "text" value ={this.state.firstName} onChange = {this.firstName} placeholder = "FirstName..."/><br/>
                 <label>lastName:</label> <input type = "text" value ={this.state.lastName} onChange = {this.lastName} placeholder = "LastName..."/><br/>
                 <label>password:</label> <input type = "text" value ={this.state.password} onChange = {this.password} placeholder = "Password..."/><br/>
                 <label>Gender:</label><select onChange = {this.genderhandler} defaultValue = "select Gender"/>
                 <select>
                 <option defaultValue>Select Gender</option>
                 <option Value = "male">Male</option>
                 <option Value = "female">Female</option>
                     </select><br/>
                     <Button as="input" type="submit" value="Submit" />
                 
             </form>
         </div>
     )
    }
}
export default Another;
