import React,{ Component} from 'react'
import './myStyles.css';
class Form extends Component{
    render(){
        return(
            <form>
                <div className = "primary">
                    <label>Username</label>
                    <input type = "text"/>
                </div>
            </form>
        )
    }
}  
export default Form
