//from is a regular HTML.it is not a controlled React Component.to convert this in to controlled component we need to follow two steps...
//the 1st step is to create a component state that will contrll the value of the in put element.
//Handling onChange events
import React,{ Component} from 'react'
import './myStyles.css';
class Form extends Component{
    constructor(props){
        super(props)
        this.state = {
            username:""
        }
    }
    handleUsernameChange = (event)=>{
     this.setState({
         username:event.target.value
     })
    }
    render(){
        return(
            <form>
                <div className = "primary">
                    <label>Username</label>
                    <input type = "text" value = {this.state.username} onChange = {this.handleUsernameChange}/>
                </div>
            </form>
        )
    }
}  
export default Form
