import React from 'react'
import Person from 'Components./Person'
function NameList(){
    const persons = [
    {
       id:1,
       name:"karthik yadav",
       age:"26",
       skill:"User Interphase"
    },
    {
        id:2,
        name:"Rajesh",
        age:"27",
        skill:"botanist"  
    },
    {
        id:3,
       name:"Joga",
       age:"27",
       skill:"botany faculty"
    },
    {
        id:4,
       name:"Girish",
       age:"26",
       skill:"botany faculty"
    }            //recomonded Re-Factor JSX into seperate component..
]
    const personList = persons.map(person =>  <Person person  = {person}/>)     
    return <div>{personList}</div>  
}
export default NameList
