import React,{ Component } from "react"
class Message extends Component{
    constructor(){
        super()
            this.State = {
                     message:"hi karthik yadav you got one message from gajalaxmi paramasivam"
            }
    }
    changeMessage(){
        this.setState({
            message:"hi baby"
        })
    }
    render(){
        return(
            <div>
                 <h1>{this.state.message}</h1>
                 <button onClick = {()=> this.changeMessage()}>open</button>
            </div>
            
        )
    }
}
export default Message;
