class Message extends Component{
    constructor(){
     super()
     this.state={
         message:"this is an alert"
     }  
    }
    changeMesaage(){
         this.setState({
             message:"make the earth more green"
         })
    }
    render(){
        return(
            <div>
          <h1>{this.state.message}</h1>
          <button ononClick = {()=>this.changeMesaage()}>open</button>
          </div>
        )
    }
}
