import React,{Component} from 'react'
class Counter extends Component{
    constructor(props){
        super(props)
        this.state = {
            count:0
        }          
    }              
    Increment(){
        /*this.setState(
            {    //setState() has two parameters 1)setState Object 2)Callback Arrow function
            count:this.state.count+1
        },
        ()=>{
            console.log('Callback value',this.state.count)   //1 is from Callback function console.log statement
        })
        console.log(this.state.count)  //0 is from Synchronous console.log*/
        this.setState((prevState)=>({
            count:prevState.count+1
        }))
        console.log(this.state.count)
    }
    IncrementFive(){
        this.Increment()   //0 locked 5 times 
        this.Increment()   //1 locked 5 times from Callback
        this.Increment()  //this behaviour of React may grouped multiple seState calls
        this.Increment()  //into a single update fro better performence all the 5 setState calls
        this.Increment()  //are done in dene in one single go updated value doesnot carry over b/w
    }      //the different calls.so whenever you have  updated the state based on the previous state we need pass
    render(){ //function as an argument to setstate() instead of passing in an object
        return(
        <div>
            <div>count-{this.state.count}</div>
            <button onClick = {()=> this.IncrementFive()}>Increment</button>
        </div>
        )
    }
}
export default Counter
