import React,{Component} from 'react'
class EventBind extends Component{
     constructor(props){
         super(props)
         this.state = {
             message:'Hello'
         }
         this.clickHandler = thi.clickHandler.bind(this)
     }
     clickHandler(){
         this.setState({
             message:"Good Bye"
         })
         console.log(this)    
     }
    render(){
        return(
            <div>  
                <div>{this.state.message}</div>
                 <button onClick = {this.clickHandler}>click</button>
            </div>
        )
    }
}
export default EventBind;
