import React,{Component} from 'react'
class Counter extends Component{
    constructor(props){
        super(props)
        this.state = {
            count:0
        }          
    }              
    Increment(){
        this.setState(
            {    //setState() has two parameters 1)setState Object 2)Callback Arrow function
            count:this.state.count+1
        },
        ()=>{
            console.log('Callback value',this.state.count)   //1 is from Callback function console.log statement
        })
        console.log(this.state.count)  //0 is from Synchronous console.log
    }
    IncrementFive(){
        this.Increment()
        this.Increment()
        this.Increment()
        this.Increment()
        this.Increment()
    }
    render(){
        return(
        <div>
            <div>count-{this.state.count}</div>
            <button onClick = {()=> this.IncrementFive()}>Increment</button>
        </div>
        )
    }
}
export default Counter
