//arrow functions in render method
import React,{Component} from 'react'
class EventBind extends Component{
     constructor(){
         super()
         this.state = {
             message:'Hello'
         }
     }
     clickHandler(){
         this.setState({
             message:"Good Bye"
         })
         console.log(this)   //this keyword refers event bind component 
     }
    render(){
        return(//we don't need the curly braces and return keyword for()=> because the function body is a single statement
            <div>  
                <div>{this.state.message}</div>
                 <button onClick = {()=>this.clickHandler()}>click</button>
            </div>
        ) //we are calling the eventHandler are returning the value that is why paranthesis is required in this approch
    }
}
export default EventBind;
